export default function Stats({ tasks }) {
  if (!tasks.length)
    return (
      <p className="stats">
        <em>Start adding some tasks to your list...</em>
      </p>
    );

  const numTasks = tasks.length;
  const numCompleted = tasks.filter((task) => task.completed).length;
  const percentage = Math.round((numCompleted / numTasks) * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You got done everything!"
          : ` ðŸ’¼ You have ${numTasks} ${
              numTasks === 1 ? "task" : "tasks"
            } on your list, and you ${
              numCompleted === 0 ? "have" : already
            } completed ${
              numCompleted === 0 ? "none" : { numCompleted }
            } (${percentage}%)`}
      </em>
    </footer>
  );
}
