import React from "react";
import "./styles.css";
import useJokeApi from "./useJokeApi";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Proramming?type=single";
  const { data, loading, error, fetchData } = useJokeApi(url);

  const handleNewJoke = () => {
    fetchData();
  };

  return (
    <div className="App">
      <h1>Joke Generator</h1>

      {loading && <p>Loading...</p>}

      {error && <p>{error}</p>}

      {!loading && data && (
        <div>
          <h2>{data.joke}</h2>
          <button className="btn" onClick={handleNewJoke}>
            New Joke
          </button>
        </div>
      )}
    </div>
  );
}
