export default function Stats({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start adding some tasks to your To-Do list ðŸš€</em>
      </p>
    );

  const numItems = items.length;
  const numCompleted = items.filter((item) => item.completed).length;
  const percentage = Math.round((numCompleted / numItems) * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You have done everything! "
          : ` ðŸ’¼ You have ${numItems} items on your list, and you already completed ${numCompleted} (${percentage}%)`}
      </em>
    </footer>
  );
}
