export default function Stats({ items }) {
  if (!items.length)
    return (
      <p className="stats">
        <em>Start adding some items to your packing list ðŸš€</em>
      </p>
    );

  const numTasks = items.length;
  const numCompleted = items.filter((item) => item.packed).length;
  const percentage = Math.round((numCompleted / numTasks) * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You got done everything!"
          : ` ðŸ’¼ You have ${numTasks} items on your list, and you already packed ${numCompleted} (${percentage}%)`}
      </em>
    </footer>
  );
}
